shader.vertex:
attribute vec3 Position;
attribute vec4 Color;
attribute vec2 TexCoord;
attribute vec4 RenderData;
attribute float Scale;

varying lowp vec4 Color0;
varying mediump vec2 TexCoord0;
varying lowp vec4 RenderDataOut;
varying lowp float ScaleOut;
uniform sampler2D Texture0;

uniform mat4 Transform;
void main(void)
{
    RenderDataOut = RenderData;
    ScaleOut = Scale;
    Color0 = Color;
    TexCoord0 = TexCoord;
    gl_Position = Transform * vec4(Position.xyz, 1.0);
}

shader.fragment:
varying lowp vec4 Color0;
varying mediump vec2 TexCoord0;
varying lowp vec4 RenderDataOut;
varying lowp float ScaleOut;
uniform sampler2D Texture0;
void main(void)
{
    vec4 Color = Color0 * texture2D(Texture0, TexCoord0);
    gl_FragColor = Color;
}

shader.vertex:
            attribute vec3 Position;
            attribute vec4 Color;
            attribute vec2 TexCoord;
            attribute vec4 RenderData;
            attribute float Scale;

			attribute float colored;
			attribute float pulse;
			attribute float shaderActive;

            varying lowp vec4 Color0;
            varying mediump vec2 TexCoord0;
            varying lowp vec4 RenderDataOut;
            varying lowp float ScaleOut;
            uniform sampler2D Texture0;
			varying float fcolored;
			varying float fpulse;
			varying float shaderActiveOut;
            uniform mat4 Transform;
            void main(void)
            {
                RenderDataOut = RenderData;
                ScaleOut = Scale;
				fcolored = colored;
				fpulse = pulse;
				shaderActiveOut = shaderActive;
                Color0 = Color;
                TexCoord0 = TexCoord;
                gl_Position = Transform * vec4(Position.xyz, 1.0);
            }

shader.fragment:
            varying lowp vec4 Color0;
            varying mediump vec2 TexCoord0;
            varying lowp vec4 RenderDataOut;
            varying lowp float ScaleOut;

			varying mediump float fcolored; 
			varying mediump float fpulse; 
			varying mediump float shaderActiveOut; 
            uniform sampler2D Texture0;
            void main(void)
            {
				if (shaderActiveOut == 0.0)
				{
					gl_FragColor = Color0 * texture2D(Texture0, TexCoord0);
				}
				else
				{
					vec4 Color = Color0 * texture2D(Texture0, TexCoord0);
					float brightness = (Color.r + Color.g + Color.b) / 5.0;
					brightness = ceil(brightness*fcolored)/fcolored;
					gl_FragColor = vec4(vec3(0.15882,0.0,0.0)*brightness*fpulse,1.0);
				}
            }

shader.vertex:
            attribute vec3 Position;
            attribute vec4 Color;
            attribute vec2 TexCoord;
            attribute vec4 RenderData;
            attribute float Scale;

			attribute float time;
			attribute float shaderActive;

            varying lowp vec4 Color0;
            varying mediump vec2 TexCoord0;
            varying lowp vec4 RenderDataOut;
            varying lowp float ScaleOut;
            uniform sampler2D Texture0;
			varying float ftime;
			varying float shaderActiveOut;
            uniform mat4 Transform;
            void main(void)
            {
                RenderDataOut = RenderData;
                ScaleOut = Scale;
				ftime = time;
				shaderActiveOut = shaderActive;
                Color0 = Color;
                TexCoord0 = TexCoord;
                gl_Position = Transform * vec4(Position.xyz, 1.0);
            }

shader.fragment:
            varying lowp vec4 Color0;
            varying mediump vec2 TexCoord0;
            varying lowp vec4 RenderDataOut;
            varying lowp float ScaleOut;

			varying mediump float ftime; 
			varying mediump float shaderActiveOut; 
            uniform sampler2D Texture0;
            void main(void)
            {
				if (shaderActiveOut == 0.0)
				{
					gl_FragColor = Color0 * texture2D(Texture0, TexCoord0);
				}
				else
				{
					vec4 Color = Color0 * texture2D(Texture0, TexCoord0);
					Color.r = 1.0-Color.r;
					Color.g = 1.0-Color.g;
					Color.b = 1.0-Color.b;
					gl_FragColor = Color*ftime;
				}
            }

shader.vertex:
attribute vec3 Position;
attribute vec4 Color;
attribute vec2 TexCoord;
attribute vec4 RenderData;
attribute float Scale;
attribute float DistortionScale;
attribute float DistortionOn;
attribute vec2 DistortionSourcePos;
varying vec4 Color0;
varying vec2 TexCoord0;
varying lowp vec4 RenderDataOut;
varying float ScaleOut;
varying float DistortionScaleOut;
varying float DistortionOnOut;
varying vec2 DistortionSourcePosOut;
uniform mat4 Transform;

void main(void)
{
	RenderDataOut = RenderData;
	ScaleOut = Scale;
	Color0 = Color;
	TexCoord0 = TexCoord;
	DistortionScaleOut = DistortionScale;
	DistortionOnOut = DistortionOn;
	DistortionSourcePosOut = DistortionSourcePos;
	gl_Position = Transform * vec4(Position.xyz, 1.0);
}

shader.fragment:
varying lowp vec4 Color0;
varying mediump vec2 TexCoord0;
varying lowp vec4 RenderDataOut;
varying lowp float ScaleOut;
varying lowp float DistortionScaleOut;
varying lowp float DistortionOnOut;
varying mediump vec2 DistortionSourcePosOut;
uniform sampler2D Texture0;

void main(void)
{
	float strength2 = DistortionScaleOut;
	vec4 col = texture2D(Texture0, TexCoord0);
	vec3 col1 = col.rgb;
	vec3 col2 = vec3(1.)-col.rgb;
	vec3 fcol = vec3(mix(col1.r, col2.r, strength2),
		mix(col1.g, col2.g, strength2),
		mix(col1.b, col2.b, strength2));
	vec3 mcol = vec3(mix(fcol.r, (fcol.r+fcol.g+fcol.b)/3., DistortionOnOut),
		mix(fcol.g, (fcol.r+fcol.g+fcol.b)/3., DistortionOnOut),
		mix(fcol.b, (fcol.r+fcol.g+fcol.b)/3., DistortionOnOut));
	col.rgb = mcol;
	gl_FragColor = Color0 * col;
}
